import React, { useState } from 'react';
import { Button } from 'react-bootstrap';
import { jwtDecode } from "jwt-decode";
import { getCookies } from './App.js';

function Game() {
    const [userName, setUserName] = useState('');
    const [error, setError] = useState(null);

    const handleSubmit = async (e) => {
        e.preventDefault(); // Éviter un comportement de soumission de formulaire par défaut

        try {
            // Récupérer le cookie
            const cookie = getCookies('cookie');
            if (cookie) {
                // Décoder le cookie pour obtenir les informations de l'utilisateur
                const decodeCookie = jwtDecode(cookie);
                console.log(decodeCookie);  // Afficher le contenu du cookie dans la console

                // Supposons que le nom d'utilisateur est stocké sous 'name'
                setUserName(decodeCookie.name); // Mettre à jour l'état avec le nom de l'utilisateur
            } else {
                setError('Cookie non trouvé');
            }
        } catch (error) {
            console.error('Erreur lors de la récupération des données utilisateur', error);
            setError('Erreur lors de la récupération des données utilisateur');
        }
    };

    return (
        <div>
            <Button type="button" onClick={handleSubmit}>
                Test
            </Button>

            {/* Affichage du nom d'utilisateur ou message d'erreur */}
            {userName && <h3>Nom d'utilisateur : {userName}</h3>}
            {error && <p style={{ color: 'red' }}>{error}</p>}
        </div>
    );
}

export default Game;
