import "./Profile.css"
import { getCookies } from "../App"
import { useNavigate } from "react-router-dom"
import React, {useEffect, useState} from "react";
import axios from 'axios';
import { jwtDecode } from "jwt-decode";
import Button from 'react-bootstrap/Button';

function ChangeUsername()
{
	const navigate = useNavigate();
	const token = getCookies('token')
	const user = jwtDecode(token);
	const [name, setName] = useState({
		'user': user.name
	});

	const handleChange = (e) => 
		{
			const {name, value} = e.target;
			setName
			({
				'name': value,
			});
		};

	const handleSubmit = async (e) =>
	{
        e.preventDefault();
        try 
        {
            await axios.patch('http://localhost:8000/api/login', name, {
                headers: {
                    'Content-Type': 'application/json',
					'Authorization': `Bearer ${token}`,
                },
                withCredentials: true,
            });
            navigate("/profile");
        } 
        catch (error)
        {
            console.error(error);
        }
	}
	return (
		<>
			<div>
			<form className='userchange-form' onSubmit={handleSubmit}>
                    <div className='form-group'>
                        <label htmlFor='name'>Change your username here</label>
                        <input className='input-form'
                            type='text'
                            id='name'
                            name='name'
                            value={user.name}
                            onChange={handleChange}
                            required
                            placeholder='new username'>    
                        </input>
                    </div>
                    <Button type='submit' className='submit-button btn'>Change</Button>
                </form>
			</div>
		</>
	)
}

function Profile()
{
	const navigate = useNavigate();
	const [user, setUser] = useState(null);

    useEffect (() => 
    {
        const fetchUserData = async () => 
        {
            try 
            {
                const token = getCookies('token');
                if (token)
                {
                    const decodeToken = jwtDecode(token);
                    const reponse = await axios.get(`http://localhost:8000/api/user/${decodeToken.id}`, {
                        headers: {
                            "Content-Type": "application/json",
                            'Authorization': `Bearer ${token}`,
                        },
                        withCredentials: true,
                    });
                    setUser(reponse.data);
                }
            }
            catch (error)
            {
                console.error('Erreur lors de la récupération des données utilisateur', error);
            }
        }

        fetchUserData();
    }, []);

	return (
		<>
			{user ? (
				<div className="profile">
					<div className="profile-container">
						<p>Nom : {user.name}</p>
						<Button className='btn rounded' onClick={ChangeUsername}>Change Username</Button>
						<p>Email : {user.email}</p>
					</div>
				</div>
            ) : (
                <p>Aucun utilisateur trouvé.</p>
            )}

		</>
	)
}

export default Profile;
