import React, { SyntheticEvent, useState } from 'react';
import axios from 'axios'
import Button from 'react-bootstrap/Button';
import LoginForm from './LoginForm';
import './RegisterForm.css'
import {useNavigate, Link, Redirect } from 'react-router-dom';


function RegisterForm()
{
    const navigate = useNavigate();
    const [redirect, setRedirect] = useState(false);
    const [formData, setFormData] = useState
    ({
        name: '',
        email: '',
        password: '',
        // password_confirm: '',
    });

    const [errorMessage, setErrorMessage] = useState('');
    const [successMessage, setSuccessMessage] = useState('');

    const handleChange = (e) =>
    {
        const {name, value} = e.target;
        setFormData
        ({
            ...formData,
            [name]: value
        });
    };

    // const handleSubmit = async (e) => 
    // {
    //     e.preventDefault();

    //     const response = await fetch('http://localhost:8000/api/register', {
    //         method: 'POST',
    //         headers: {'Content-Type': 'application/json'},
    //         body: JSON.stringify({
    //             name,
    //             email,
    //             password
    //         })
    //     });
    //     const content = await response.json();

    //     console.log(content);
    // }
    const handleSubmit = async (e) =>
    {
        e.preventDefault();
        const data = new FormData();
        for (const [key, value] of Object.entries(formData))
        {
            data.append(key, value);
        }
        try 
        {
            const response = await axios.post('http://localhost:8000/users/register', data, {
                headers: {
                    'Content-Type': 'multipart/form-data',
                }
            });
            setRedirect(true);
            alert(response.data.message);
        } 
        catch (error)
        {
            alert("Error! Bad Input!");
            console.error(error);
        }
    };

    if(redirect)
    {
        return <Redirect to="/home"/>
    }

    return (
        <>
            <div className='form-container'>
                <div className='form-box'>
                    <h2>Register here</h2>
                    <form className='registration-form' onSubmit={handleSubmit}>
                        <div className='form-group'>
                            <label htmlFor='name'>Username:</label>
                            <input className='input-form'
                                type='text'
                                id='name'
                                name='name'
                                value={formData.name}
                                onChange={handleChange}
                                required
                                placeholder='Username'>
                            </input>
                        </div>
                        <div className='form-group'>
                            <label htmlFor="email">Mail:</label>
                            <input className='input-form'
                                type='email'
                                id='email'
                                name='email'
                                value={formData.mail}
                                onChange={handleChange}
                                required
                                placeholder='Email'>
                            </input>
                        </div>
                        <div className='form-group'>
                            <label htmlFor='password'>Password:</label>
                            <input className='input-form'
                                type='password'
                                id='password'
                                name='password'
                                value={formData.password}
                                onChange={handleChange}
                                required
                                placeholder='Password'>
                            </input>
                        </div>
                        {/* <div className='form-group'>
                            <label htmlFor='password_confirm'>Confirm Password:</label>
                            <input className='input-form'
                                type='password'
                                id='password_confirm'
                                name='password_confirm'
                                value={formData.password_confirm}
                                onChange={handleChange}
                                required
                                placeholder='Confirm Password'>
                            </input>
                        </div> */}

                        {errorMessage && <p className='error-message'>{errorMessage}</p>}
                        {successMessage && <p className='success-manage'>{successMessage}</p>}

                        <Button type='submit' className='submit-button' class="btn btn-primary p-2 border border-0 rounded-pill">Register</Button>
                    </form>
                    <div className='login-text'>
                        <p>Already have an account ?</p>

                        <div>
                            <Link to='/login'>Login</Link>
                        </div>
                    </div>
                </div>
            </div>
        </>
    );
}

export default RegisterForm;
