import React, { useState } from 'react';
import axios from 'axios'
import Button from 'react-bootstrap/Button';
import LoginForm from './LoginForm';
import './RegisterForm.css'
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';


function RegisterForm()
{
    const [formData, setFormData] = useState
    ({
        pseudo: '',
        mail: '',
        password: '',
        password_confirm: '',
        pp: null,
    });

    const [errorMessage, setErrorMessage] = useState('');
    const [successMessage, setSuccessMessage] = useState('');

    const handleChange = (e) => 
    {
        const {name, value} = e.target;
        setFormData
        ({
            ...formData,
            [name]: value
        });
    };

    const handleSubmit = async (e) =>
    {
        e.preventDefault();
        const data = new FormData();
        for (const [key, value] of Object.entries(formData))
        {
            data.append(key, value);
        }
        try 
        {
            const response = await axios.post('http://localhost:8000/users/register', data, {
                headers: {
                    'Content-Type': 'multipart/form-data',
                }
            });
            alert(response.data.message);
        } 
        catch (error)
        {
            alert("Error! Bad Input!");
            console.error(error);
        }
    };


    return (
            <div className='form-container'>
                <div className='form-box'>
                    <h2>User Registration</h2>
                    <form className='registration-form' onSubmit={handleSubmit}>
                        <div className='form-group'>
                            <label htmlFor='pseudo'>Username:</label>
                            <input className='input-form'
                                type='text'
                                id='pseudo'
                                name='pseudo'
                                value={formData.pseudo}
                                onChange={handleChange}
                                required>
                            </input>
                        </div>
                        <div className='form-group'>
                            <label htmlFor="mail">Mail:</label>
                            <input className='input-form'
                                type='email'
                                id='mail'
                                name='mail'
                                value={formData.mail}
                                onChange={handleChange}
                                required>
                            </input>
                        </div>
                        <div className='form-group'>
                            <label htmlFor='password'>Password:</label>
                            <input className='input-form'
                                type='password'
                                id='password'
                                name='password'
                                value={formData.password}
                                onChange={handleChange}
                                required>
                            </input>
                        </div>
                        <div className='form-group'>
                            <label htmlFor='password_confirm'>Confirm Password:</label>
                            <input className='input-form'
                                type='password'
                                id='password_confirm'
                                name='password_confirm'
                                value={formData.password_confirm}
                                onChange={handleChange}
                                required>
                            </input>
                        </div>

                        {errorMessage && <p className='error-message'>{errorMessage}</p>}
                        {successMessage && <p className='success-manage'>{successMessage}</p>}

                        <Button type='submit' className='submit-button' class="btn btn-primary p-2 border border-0 rounded-pill">Register</Button>
                    </form>
                    <p>Already have an account ?</p>

                    <div>
                        <div>
                            <Link to="/login" class="text-decoration-none text-dark">login</Link>
                        </div>
                        <Routes>
                        <Route path="/login" element={<LoginForm />} />
                        </Routes>
                    </div>
                </div>
            </div>
    );
}

export default RegisterForm;
