import "./Profile.css"
import { getCookies } from "../App"
import { useNavigate } from "react-router-dom"
import React, {useEffect, useState} from "react";
import axios from 'axios';
import { jwtDecode } from "jwt-decode";
import Button from 'react-bootstrap/Button';

function ChangeUsername()
{
	const navigate = useNavigate();
	const user = jwtDecode(getCookies('token'));
	const [name, setName] = useState({
		'user': ''
	});

	const handleSubmit = async (e) =>
	{
        e.preventDefault();
        const data = new FormData();
        for (const [key, value] of Object.entries(formData))
            data.append(key, value);
        try 
        {
            await axios.post('http://localhost:8000/api/login', data, {
                headers: {
                    'Content-Type': 'multipart/form-data',
                },
                withCredentials: true,
            });
            setIsAuthenticated(true);
            navigate("/home");
        } 
        catch (error)
        {
            console.error(error);
        }
	}

}

function Profile()
{
	const navigate = useNavigate();
	const [user, setUser] = useState(null);

    useEffect (() => 
    {
        const fetchUserData = async () => 
        {
            try 
            {
                const token = getCookies('token');
                if (token)
                {
                    const decodeToken = jwtDecode(token);
                    const reponse = await axios.get(`http://localhost:8000/api/user/${decodeToken.id}`, {
                        headers: {
                            "Content-Type": "application/json",
                            'Authorization': `Bearer ${token}`,
                        },
                        withCredentials: true,
                    });
                    setUser(reponse.data);
                }
            }
            catch (error)
            {
                console.error('Erreur lors de la récupération des données utilisateur', error);
            }
        }

        fetchUserData();
    }, []);

	return (
		<>
			{user ? (
				<div className="profile">
					<div className="profile-container">
						<p>Nom : {user.name}</p>
						<Button className='btn rounded'>Change Username</Button>
						<p>Email : {user.email}</p>
					</div>
				</div>
            ) : (
                <p>Aucun utilisateur trouvé.</p>
            )}

		</>
	)
}

export default Profile;
